class Fossil extends React.Component{
    constructor(props){
      super(props);
      this.state = {
        data: []
      }
    }

    componentDidMount() {
      fetch('https://acnhapi.com/v1a/fossils')
          .then(res => res.json())
          .then(result => {
            this.setState({data: result});
          })
    }

